import { OAuth } from 'oauth';

interface IConstructorOptions {
  consumerKey: string
  consumerSecret: string
  callbackUrl: string
}

interface ICallbackOptions {
  token: string
  tokenSecret: string
  verifier: string
}

interface IRequestResponse {
  token: string
  tokenSecret: string
  callbackConfirmed: string
  redirectUrl: string
}

interface ICallbackResponse {
  userId: string
  userName: string
  userToken: string
  userTokenSecret: string
}

declare class AsyncTwitterLogin {
    consumerKey: string;
    consumerSecret: string;
    callbackUrl: string;
    oauth: OAuth;
    constructor({ consumerKey, consumerSecret, callbackUrl }: IConstructorOptions);
    request(): Promise<IRequestResponse>;
    callback({ token, tokenSecret, verifier }: ICallbackOptions): Promise<ICallbackResponse>;
}

export { AsyncTwitterLogin };
